mapper.select:
select g.*, gl.group_nm from (select * from GROUPFILE where login_id = #{login_id} ) g left join GROUP_LIST gl on g.group_no = gl.group_no
and gfilename like concat('%',#{cri.title},'%')
and g.group_no = #{cri.group_no}
order by g.gregdate desc limit #{cri.pageStart}, #{cri.amount}

select g.*, gl.group_nm from (select * from GROUPFILE where group_no = (select group_no from group_join where login_id = #{login_id} and APRV_YN = "Y") ) g left join GROUP_LIST gl on g.group_no = gl.group_no
and gfilename like concat('%',#{cri.title},'%')
limit #{cri.pageStart}, #{cri.amount}

mapper.update:
update GROUPFILE set
gfilename = #{gfilename},
gfilecon = #{gfilecon}
where gnumber = #{gnumber}

mapper.select:
		select count(*) as total
		from GROUPFILE
		where login_id = #{login_id}


            and gfilename like concat('%',#{cri.title},'%')


            and group_no = #{cri.group_no}
