mapper.select:
select a.*, b.average_score , b.percentage from (select * from HOMEWORK where login_id = #{login_id}) a left join (SELECT homework_no, ROUND(AVG(RECORD_SCORE)) AS average_score, Round(SUM(CASE WHEN RECORD_SCORE = 100 THEN 1 ELSE 0 END)/count(*)*100) AS percentage FROM HOMEWORKRECORD GROUP BY homework_no) b on a.homework_no = b.homework_no
and homework_title like concat('%',#{cri.title},'%')
order by homework_register desc limit #{cri.pageStart}, #{cri.amount}

select s.*, h.homework_title, h.homework_language, h.HOMEWORK_LEVEL, h.category from (select * from HOMEWORKSUBMIT where login_id = #{login_id}) s left join HOMEWORK h on h.homework_no= s.homework_no
and homework_title like concat('%',#{cri.title},'%')
order by submit_date desc limit #{cri.pageStart}, #{cri.amount}

select a.*, b.group_nm
from (
select m.*, j.group_no
from GROUP_JOIN j
left join MEMBERS m on j.login_id = m.login_id
where APRV_YN = "Y"
) a
join (
select group_no, group_nm
from GROUP_LIST
where login_id = #{login_id}

    and group_nm = #{group_nm}

) b
on a.group_no = b.group_no
where not exists (
select 1
from HOMEWORKSUBMIT
where homework_no = #{homework_no}
and login_id = a.login_id
);

mapper.update:
update HOMEWORKSUBMIT
set HOMEWORK_ST = "제출", homework_recieve = now(), HOMEWORK_SCORE = #{score}
where login_id = #{login_id} and homework_no = #{homework_no};

mapper.select:
select a.*,b.HOMEWORK_LANGUAGE, b.HOMEWORK_LEVEL, b.HOMEWORK_TITLE,b.category from (select * from HOMEWORKSUBMIT where login_id = #{login_id}) a
                                                                            left join HOMEWORK b
                                                                                      on a.homework_no = b.homework_no;

select b.*,a.record_code, a.record_score, a.record_date from (select * from HOMEWORKRECORD where login_id = #{login_id} ) a left join HOMEWORK b on a.homework_no = b.homework_no
and homework_title like concat('%',#{cri.title},'%')
order by RECORD_DATE desc limit #{cri.pageStart}, #{cri.amount}
